"$:/plugins/telmiger/Tix/elements/buttons-editor.css": {
    "created": "20220205143821685",
    "creator": "Thomas Elmiger",
    "text": "/* Standard editor buttons */

.tc-tiddler-frame:not(.lox-tiddler) .tc-tiddler-title .tc-tiddler-controls {
   font-size: 1.5em;
   flex-grow: 1;
   position: relative;
   display: flex;
   align-content: end;
   flex-direction: row;
   justify-content: start;
   gap: 0.25em;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-text {
   margin: 0 0.25em 0 0;
   display: block;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-tiddler-edit-title button {
   margin: 0.25em 0.25em 0.25em 0;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-tiddler-edit-title button svg {
   font-size: 2rem;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-titlebar {
   overflow: initial;
}


/* borderless icon only buttons */

.tc-tiddler-frame:not(.lox-tiddler) .tc-tiddler-edit-title .tc-btn-invisible svg:last-child {
   width: 1.75rem;
   height: 1.75rem;
   margin: 0 0.25rem 0.25rem 0;
}


/* Rounded */

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-rounded {
   font-size: 0.73em;
   margin: 0 0 0 0.125em;
   padding: 0 0.3em 0.2em 0.4em;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-rounded svg:first-child {
   margin-left: -.05em;
   margin-right: -0.025em;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-rounded .tc-btn-text {
   font-size: 0.95em;
}


/* Boxed */

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-boxed {
   font-size: 0.7em;
   margin: 0 0 0 0.125em;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-boxed svg:first-child {
   margin-left: 0.05em;
}

.tc-tiddler-frame:not(.lox-tiddler) .tc-btn-boxed .tc-btn-text:first-child {
   padding-left: 0.25em;
}",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/elements/buttons-editor.css",
    "modified": "20220306123917488",
    "modifier": "Thomas Elmiger",
