"$:/plugins/telmiger/Tix/elements/meta.css": {
    "created": "20220122141145483",
    "creator": "Thomas Elmiger",
    "text": "/* Container */

.lox-meta, .lox-created, .lox-modified {
   display: flex;
   flex-direction: row;
   flex-wrap: wrap;
}

.lox-meta {
   line-height: 1.6em;
   margin: 0.6em 0;
   width: 23em;
   max-width: 100%;
   gap: 1em;
   flex-grow: 1;
   flex-shrink: 1;
}

.lox-meta:first-child {
   max-width: 93%;
}

.lox-meta:not(:first-child) {
   justify-content: end;
}

.lox-meta:last-child .lox-created,
.lox-meta:last-child .lox-modified {
   justify-content: end;
}

.lox-meta:last-child .lox-modified {
   padding-left: 0.75em;
}

.lox-meta:first-child .lox-created {
   padding-right: 0.75em;
}


/* Dates */

.lox-created:before {
   content: "🗒️";
   padding-right: 0.3em;
}

.lox-modified:before {
   content: "✏️ ";
   padding-right: .3em;
}


/* Users */

.lox-creator, .lox-modifier {
   padding-left: 0.3em;
}
",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/elements/meta.css",
    "modified": "20220320102318838",
    "modifier": "Thomas Elmiger",
    "list-after": "$:/plugins/telmiger/Tix/styles/styles.css"
},

"$:/plugins/telmiger/Tix/elements/tags.css": {
    "created": "20220122141603544",
    "creator": "Thomas Elmiger",
    "text": ".lox-tags-list {
   flex-grow: 1;
/*   width: 27em; */
   min-width: 20%;
   max-width: 100%;
   margin: 0.75em 0 0.25em 0;
}

.lox-tags-list .tc-tags-wrapper {
   justify-content: center;
   margin: 0;
}

.lox-tags-list:first-child .tc-tags-wrapper {
   justify-content: start;
}

.lox-tags-list:last-child .tc-tags-wrapper {
   justify-content: end;
}

.lox-tags-list .tc-tags-wrapper {
   display: flex;
   flex-direction: row;
   gap: 0.4em;
   flex-wrap: wrap;
}

.lox-tags-list .tc-tags-wrapper .tc-tag-list-item {
   margin: 0;
}",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/elements/tags.css",
    "modified": "20220306123119020",
    "modifier": "Thomas Elmiger",
    "list-after": "$:/plugins/telmiger/Tix/styles/styles.css"
},

"$:/plugins/telmiger/Tix/elements/tiddler-icon-center.css": {
    "created": "20220129160324317",
    "creator": "Thomas Elmiger",
    "text": ".lox-tiddler-icon > span,
.lox-tiddler-icon > a > span {
   line-height: 1;
}

.lox-tiddler-icon .lox-icon svg {
   width: 100%;
   height: 100%;
   padding: 0;
   margin: 0;
}

.lox-tiddler-icon, .lox-tiddler-icon a, .lox-tiddler-icon .lox-icon  {
   display: block;
   width: 100%;
   height: 100%;
   padding: 0;
   margin: 0;
}

.lox-tiddler .lox-tiddler-header .lox-tiddler-icon {
   position: absolute;
   width: 2em;
   height: 2em;
   margin: 0;
   top: -1em;
   left: calc(50% - 1em);
   font-size: 2em;
   padding: 0.45em;
   border-radius: 1em;
   border: 2px solid <<colour tiddler-border>>;
   background: <<colour tiddler-background>>;
}

.lox-tiddler-bottom {
  margin-bottom: 2.5em;
}


/* add space at the top for sticky titles */

<$list filter="[[$:/themes/tiddlywiki/vanilla/options/stickytitles]get[text]prefix[yes]]">

.lox-tiddler .tc-tiddler-title {
   border-top: 2.25em solid <<colour tiddler-border>>;
}

</$list>
",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/elements/tiddler-icon-center.css",
    "modified": "20220318223519799",
    "modifier": "Thomas Elmiger",
    "list-after": "$:/plugins/telmiger/Tix/elements/tiddler-id.css ",
    "code-body": "yes"
},

"$:/plugins/telmiger/Tix/elements/tiddler-icon.css": {
    "created": "20220130125133449",
    "creator": "Thomas Elmiger",
    "text": "/* Tiddler icon */

.lox-tiddler-icon {
   margin: 0;
   color: <<colour foreground>>
}

.lox-tiddler-icon svg {
   overflow: visible;
}

.lox-tiddler-icon a {
   color: <<colour foreground>>
}


/* at the top before id (title) */

.lox-tiddler-top > .lox-tiddler-icon {
   display: inline-block;
   margin-right: 0.5em;
   width: 1.1em;
   height: 1.1em;
   margin-left: 0.1em;
   position: relative;
   top: 0.175em;
}

.lox-tiddler-top > .lox-tiddler-icon svg {
   width: 1.1em;
   height: 1.1em;
   padding: 0;
   margin-bottom: 0.125em;
}

/* at the top as the last element */

.lox-tiddler-top .lox-tiddler-icon:last-child {
   font-size: 1.5em;
   margin-left: 1em;
   width: 1em;
   height: 1em;
   margin-right: 0.1em;
   position: relative;
   top: 0.175em;
}


/* in the header */

.lox-tiddler-header .lox-tiddler-icon {
   width: 1.75em;
   position: relative;
   top: 0.6em;
}",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/elements/tiddler-icon.css",
    "modified": "20220220112416116",
    "modifier": "Thomas Elmiger",
