.lox-tiddler-header, .lox-tiddler-body, .lox-tiddler-footer {
   border: 1px solid <<colour tiddler-border>>;
   border-width: 0 1px 0 1px;
}


 /* Borders top and bottom */

.lox-tiddler-header {
   border-top: 1px solid <<colour tiddler-border>>;
}

.lox-tiddler-footer {
   border-bottom: 1px solid <<colour tiddler-border>>;
   padding: 0.75rem 5% 1rem 5%;
}

/* Header hidden? => Top border on body */

<$list filter="""[all[shadows+tiddlers]suffix[styles/hide/header.css]tag[$:/tags/Stylesheet]]""">

.lox-tiddler-body {
   border-top: 1px solid <<colour tiddler-border>>;
}

</$list>


/* Footer hidden? => Bottom border on body */

<$list filter="""[all[shadows+tiddlers]suffix[styles/hide/footer.css]tag[$:/tags/Stylesheet]]""">

.lox-tiddler-body {
   border-bottom: 1px solid <<colour tiddler-border>>;
}

</$list>


 /* Tiddler info, CLASSIC ?! */

.lox-tiddler-info {

}",
