"$:/plugins/telmiger/Tix/styles/parts/parts.css": {
    "created": "20220220093129349",
    "creator": "Thomas Elmiger",
    "text": " /* Tiddler top, header, footer, bottom */

.lox-tiddler-top, .lox-tiddler-header, .lox-tiddler-footer, .lox-tiddler-bottom {
   position: relative;
   display: flex;
   flex-direction: row;
   flex-wrap: wrap;
   justify-content: space-between;
   align-items: baseline;
}


 /* Tiddler header, info, body and footer */

.lox-tiddler-header, .lox-tiddler-info,
.lox-tiddler-body, .lox-tiddler-footer {
   background-color: <<colour tiddler-background>>;
}


/* Single parts */

.lox-tiddler-top {
   padding-top: 0.25rem;
   padding-bottom: 0.125rem;
}

.lox-tiddler-header {
   align-items: center;
   padding: 0 5% 0.5rem 5%;
}

.lox-tiddler-info.tc-tiddler-info {
   width: 100%;
   margin: 0;
   margin-bottom: 1rem;
}

.lox-tiddler-body {
   position: relative;
   padding: 1.25rem 5% 1em 5%;
}

.lox-tiddler-footer {
   background: <<colour table-header-background>>;
   border: solid <<colour tiddler-background>> 0.5rem;
   padding: 1rem calc(5% - 0.5rem) 1.25rem calc(5% - 0.5rem);
}",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/styles/parts/parts.css",
    "modified": "20220306122744353",
    "modifier": "Thomas Elmiger"
},

