.lox-tiddler-header, .lox-tiddler-info, .lox-tiddler-body, .lox-tiddler-footer {
  -webkit-box-shadow: 4px 4px 4px rgba(0, 0, 0, 0.2);
     -moz-box-shadow: 4px 4px 4px rgba(0, 0, 0, 0.2);
          box-shadow: 4px 4px 4px rgba(0, 0, 0, 0.2);
}",
