"$:/plugins/telmiger/Tix/styles/styles.css": {
    "created": "20220111192533453",
    "creator": "Thomas Elmiger",
    "text": "/* Tiddler frame */

.lox-tiddler.tc-tiddler-view-frame {
   width: 100%;
/*   max-width: 54rem; */
   box-shadow:none;
   border: none;
   background-color: transparent;
   padding: 0;
}

.tc-story-river .tc-tiddler-frame {
   margin: 0;
}


/* Sticky titles */

<$list filter="[[$:/themes/tiddlywiki/vanilla/options/stickytitles]get[text]prefix[yes]]">

.lox-tiddler .tc-tiddler-title {
   position: -webkit-sticky;
   position: -moz-sticky;
   position: -o-sticky;
   position: -ms-sticky;
   position: sticky;
   top: 0px;
   z-index: 500;
}

</$list>


/* Hide stuff like button text */

.visually-hidden {
   {{$:/plugins/telmiger/Tix/styles/visually-hidden}}
}


/* Fix for reveal widgets */

[hidden="true"] {
   display: none;
}",
