"$:/plugins/telmiger/Tix/toolbars/styles.css": {
    "created": "20220122133606907",
    "creator": "Thomas Elmiger",
    "text": "/* Standard Tiddler toolbar*/

/* fix for vanilla */

.lox-tiddler .tc-tiddler-controls > span > button, 
.lox-tiddler .tc-tiddler-controls > span > span > button, 
.lox-tiddler .tc-tiddler-controls > span > span > span > button {
   margin: 0;
}

.lox-tiddler-toolbar {
   font-size: 1.5em;
   flex-grow: 1;
   position: relative;
   display: flex;
   align-content: end;
   flex-direction: row;
   justify-content: start;
   gap: 0.25em;
}


/* Buttons */

.lox-tiddler-toolbar .tc-btn-text {
   margin-left: 0;
   vertical-align: middle;
}

.lox-tiddler-toolbar button.tc-btn-invisible {
   font-size: 1.5em;
}

/* decrease size if text is present */

.lox-tiddler-toolbar .tc-btn-invisible .tc-image-button:not(:last-child) {
   font-size: 0.875em;
}

.lox-tiddler-toolbar button.tc-btn-invisible .tc-btn-text {
   font-size: 1.1rem;
}

/* increase margin if icons are off */

.lox-tiddler-toolbar .tc-btn-boxed .tc-btn-text:first-child {
  margin-left: 0.3em;
}


/* Improve rounded buttons (TW standard) */

.lox-tiddler .lox-tiddler-toolbar .tc-btn-rounded {
   font-size: 0.666em;
   margin: 0.75em 0 0 0;
   margin-bottom: 0.25em;
   padding: 0 0.3em 0.2em 0.4em;
   height: 2.5em;
   min-width: 2.5em;
}

.lox-tiddler .lox-tiddler-toolbar .tc-btn-rounded .tc-btn-text {
   margin-right: 0.4em;
   font-size: 1.05em;
}

.lox-tiddler .lox-tiddler-toolbar .tc-btn-rounded .tc-btn-text:first-child {
  margin-left: 0.3em;
}


/* Improve boxed buttons (TW standard) */

.lox-tiddler .lox-tiddler-toolbar .tc-btn-boxed {
   font-size: 1rem;
   padding: 0.25rem;
   margin: 0.75em 0 0 0;
   margin-bottom: 0.25em;
   background: none;
   border: 1px solid rgb(128, 128, 128);
   border-radius: 0.25em;
}

.lox-tiddler .lox-tiddler-toolbar .tc-btn-boxed .tc-btn-text {
   font-size: 0.875em;
   margin-right: 0.25em;
}


/* Dropdowns like more, export tiddler */

.lox-tiddler-toolbar .tc-drop-down a, 
.lox-tiddler-toolbar .tc-drop-down button {
   font-size: 1.1rem;
   line-height: 1.25;
   margin-bottom: 0.25rem;
}

.lox-tiddler .lox-tiddler-toolbar .tc-popup .tc-drop-down a svg, 
.lox-tiddler .lox-tiddler-toolbar .tc-popup .tc-drop-down button svg {
   font-size: 1.1rem;
   margin-top: 0.125rem;
   margin-right: 0.25rem;
}

.lox-tiddler-toolbar span {
   line-height: 0;
}

.lox-tiddler-toolbar:last-child {
   justify-content: end;
}

.lox-tiddler-toolbar:not(:first-child) {
  justify-content: end;
}


/* buttons in the footer (smaller) */

.lox-tiddler-footer .lox-tiddler-toolbar .tc-btn-boxed {
   font-size: 0.85rem;
}",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/telmiger/Tix/toolbars/styles.css",
    "modified": "20220306223020658",
    "modifier": "Thomas Elmiger",
    "list-after": "$:/plugins/telmiger/Tix/styles/styles.css"
},

